INSERT INTO passage (address, title, text) SELECT 'Genesis 2:16–17', 'God’s Command to Adam', 'And the LORD God commanded the man, saying, “You may surely eat of every tree of the garden, but of the tree of the knowledge of good and evil you shall not eat, for in the day that you eat of it you shall surely die.”' WHERE NOT EXISTS (SELECT 1 FROM passage WHERE address='Genesis 2:16–17' AND title='God’s Command to Adam');
