INSERT INTO question (text, question_type, difficulty, metadata, skill_id, passage_id) VALUES ('What word in God’s command to Adam emphasizes the consequence of eating from the forbidden tree?', 'MCQ', 'EASY', '{"options":{"A":"Die","B":"Eat","C":"Good","D":"Take"},"correctAnswer":"A"}', (SELECT id FROM skill WHERE name='Identifying Key Words and Phrases'), (SELECT id FROM passage WHERE address='Genesis 2:16–17' AND title='God’s Command to Adam'));
INSERT INTO passage (address, title, text) SELECT 'Genesis 3:1–5', 'The Serpent’s Question to Eve', 'Now the serpent was more crafty than any other beast of the field that the LORD God had made. He said to the woman, “Did God actually say, ‘You shall not eat of any tree in the garden’?” And the woman said to the serpent, “We may eat of the fruit of the trees in the garden, but God said, ‘You shall not eat of the fruit of the tree that is in the midst of the garden, neither shall you touch it, lest you die.’” But the serpent said to the woman, “You will not surely die. For God knows that when you eat of it your eyes will be opened, and you will be like God, knowing good and evil.”' WHERE NOT EXISTS (SELECT 1 FROM passage WHERE address='Genesis 3:1–5' AND title='The Serpent’s Question to Eve');
INSERT INTO skill (name, category, learning_stage, subject_id) SELECT 'Identifying Key Words and Phrases', 'VOCABULARY_AND_LANGUAGE', 'FOUNDATIONAL', (SELECT id FROM subject WHERE name='Reading') WHERE NOT EXISTS (SELECT 1 FROM skill WHERE name='Identifying Key Words and Phrases');
INSERT INTO question (text, question_type, difficulty, metadata, skill_id, passage_id) VALUES ('What key phrase did the serpent use to introduce doubt in Eve’s mind?', 'MCQ', 'EASY', '{"options":{"A":"You must not touch it","B":"Surely you will not die","C":"The tree is good for food","D":"You are like God"},"correctAnswer":"B"}', (SELECT id FROM skill WHERE name='Identifying Key Words and Phrases'), (SELECT id FROM passage WHERE address='Genesis 3:1–5' AND title='The Serpent’s Question to Eve'));
INSERT INTO passage (address, title, text) SELECT 'Exodus 3:13–14', 'God Reveals His Name to Moses', 'Then Moses said to God, “If I come to the people of Israel and say to them, ‘The God of your fathers has sent me to you,’ and they ask me, ‘What is his name?’ what shall I say to them?” God said to Moses, “I AM WHO I AM.” And he said, “Say this to the people of Israel: ‘I AM has sent me to you.’”' WHERE NOT EXISTS (SELECT 1 FROM passage WHERE address='Exodus 3:13–14' AND title='God Reveals His Name to Moses');
INSERT INTO skill (name, category, learning_stage, subject_id) SELECT 'Identifying Key Words and Phrases', 'VOCABULARY_AND_LANGUAGE', 'FOUNDATIONAL', (SELECT id FROM subject WHERE name='Reading') WHERE NOT EXISTS (SELECT 1 FROM skill WHERE name='Identifying Key Words and Phrases');
INSERT INTO question (text, question_type, difficulty, metadata, skill_id, passage_id) VALUES ('What phrase did God use to describe Himself to Moses?', 'MCQ', 'EASY', '{"options":{"A":"Lord of Hosts","B":"The Creator of All","C":"The Most High","D":"I AM WHO I AM"},"correctAnswer":"D"}', (SELECT id FROM skill WHERE name='Identifying Key Words and Phrases'), (SELECT id FROM passage WHERE address='Exodus 3:13–14' AND title='God Reveals His Name to Moses'));
INSERT INTO passage (address, title, text) SELECT 'Exodus 20:1–3', 'The First Commandment', 'And God spoke all these words, saying, “I am the LORD your God, who brought you out of the land of Egypt, out of the house of slavery. “You shall have no other gods before me.' WHERE NOT EXISTS (SELECT 1 FROM passage WHERE address='Exodus 20:1–3' AND title='The First Commandment');
