INSERT INTO question (text, question_type, difficulty, metadata, skill_id, passage_id) VALUES ('What key phrase in the first commandment shows God’s demand for exclusive worship?', 'MCQ', 'EASY', '{"options":{"A":"Remember my name","B":"You shall have no other gods before me","C":"I am the Lord your God","D":"Keep my statutes"},"correctAnswer":"B"}', (SELECT id FROM skill WHERE name='Identifying Key Words and Phrases'), (SELECT id FROM passage WHERE address='Exodus 20:1–3' AND title='The First Commandment'));
INSERT INTO passage (address, title, text) SELECT 'Numbers 14:6–9', 'Caleb and Joshua’s Report on Canaan', 'And Joshua the son of Nun and Caleb the son of Jephunneh, who were among those who had spied out the land, tore their clothes and said to all the congregation of the people of Israel, “The land, which we passed through to spy it out, is an exceedingly good land. If the LORD delights in us, he will bring us into this land and give it to us, a land that flows with milk and honey. Only do not rebel against the LORD. And do not fear the people of the land, for they are bread for us. Their protection is removed from them, and the LORD is with us; do not fear them.”' WHERE NOT EXISTS (SELECT 1 FROM passage WHERE address='Numbers 14:6–9' AND title='Caleb and Joshua’s Report on Canaan');
INSERT INTO skill (name, category, learning_stage, subject_id) SELECT 'Identifying Key Words and Phrases', 'VOCABULARY_AND_LANGUAGE', 'FOUNDATIONAL', (SELECT id FROM subject WHERE name='Reading') WHERE NOT EXISTS (SELECT 1 FROM skill WHERE name='Identifying Key Words and Phrases');
INSERT INTO question (text, question_type, difficulty, metadata, skill_id, passage_id) VALUES ('What key phrase did Caleb and Joshua use to encourage Israel?', 'MCQ', 'EASY', '{"options":{"A":"The land is dangerous","B":"We should wait and see","C":"The Lord is with us","D":"We must leave now"},"correctAnswer":"C"}', (SELECT id FROM skill WHERE name='Identifying Key Words and Phrases'), (SELECT id FROM passage WHERE address='Numbers 14:6–9' AND title='Caleb and Joshua’s Report on Canaan'));
INSERT INTO passage (address, title, text) SELECT 'Deuteronomy 6:4–5', 'The Shema: Israel’s Greatest Commandment', '“Hear, O Israel: The LORD our God, the LORD is one. You shall love the LORD your God with all your heart and with all your soul and with all your might.' WHERE NOT EXISTS (SELECT 1 FROM passage WHERE address='Deuteronomy 6:4–5' AND title='The Shema: Israel’s Greatest Commandment');
