INSERT INTO question (text, question_type, difficulty, metadata, skill_id, passage_id) VALUES ('What key phrase emphasizes the singularity of God in the Shema?', 'MCQ', 'EASY', '{"options":{"A":"The Lord our God, the Lord is one","B":"Teach them to your children","C":"Keep these words in your heart","D":"You shall love the Lord"},"correctAnswer":"A"}', (SELECT id FROM skill WHERE name='Identifying Key Words and Phrases'), (SELECT id FROM passage WHERE address='Deuteronomy 6:4–5' AND title='The Shema: Israel’s Greatest Commandment'));
INSERT INTO passage (address, title, text) SELECT 'Joshua 1:6–9', 'God’s Encouragement to Joshua', 'Be strong and courageous, for you shall cause this people to inherit the land that I swore to their fathers to give them. Only be strong and very courageous, being careful to do according to all the law that Moses my servant commanded you. Do not turn from it to the right hand or to the left, that you may have good success wherever you go. This Book of the Law shall not depart from your mouth, but you shall meditate on it day and night, so that you may be careful to do according to all that is written in it. For then you will make your way prosperous, and then you will have good success. Have I not commanded you? Be strong and courageous. Do not be frightened, and do not be dismayed, for the LORD your God is with you wherever you go.”' WHERE NOT EXISTS (SELECT 1 FROM passage WHERE address='Joshua 1:6–9' AND title='God’s Encouragement to Joshua');
INSERT INTO skill (name, category, learning_stage, subject_id) SELECT 'Identifying Key Words and Phrases', 'VOCABULARY_AND_LANGUAGE', 'FOUNDATIONAL', (SELECT id FROM subject WHERE name='Reading') WHERE NOT EXISTS (SELECT 1 FROM skill WHERE name='Identifying Key Words and Phrases');
INSERT INTO question (text, question_type, difficulty, metadata, skill_id, passage_id) VALUES ('What key phrase is repeated multiple times in Joshua 1 to emphasize courage?', 'MCQ', 'EASY', '{"options":{"A":"Lead carefully","B":"Be strong and courageous","C":"You must be cautious","D":"Listen to the elders"},"correctAnswer":"B"}', (SELECT id FROM skill WHERE name='Identifying Key Words and Phrases'), (SELECT id FROM passage WHERE address='Joshua 1:6–9' AND title='God’s Encouragement to Joshua'));
INSERT INTO passage (address, title, text) SELECT 'Judges 6:12–16', 'God Calls Gideon', 'And the angel of the LORD appeared to him and said to him, “The LORD is with you, O mighty man of valor.” And Gideon said to him, “Please, my lord, if the LORD is with us, why then has all this happened to us? And where are all his wonderful deeds that our fathers recounted to us, saying, ‘Did not the LORD bring us up from Egypt?’ But now the LORD has forsaken us and given us into the hand of Midian.” And the LORD turned to him and said, “Go in this might of yours and save Israel from the hand of Midian; do not I send you?” And he said to him, “Please, Lord, how can I save Israel? Behold, my clan is the weakest in Manasseh, and I am the least in my father’s house.” And the LORD said to him, “But I will be with you, and you shall strike the Midianites as one man.”' WHERE NOT EXISTS (SELECT 1 FROM passage WHERE address='Judges 6:12–16' AND title='God Calls Gideon');
INSERT INTO skill (name, category, learning_stage, subject_id) SELECT 'Identifying Key Words and Phrases', 'VOCABULARY_AND_LANGUAGE', 'FOUNDATIONAL', (SELECT id FROM subject WHERE name='Reading') WHERE NOT EXISTS (SELECT 1 FROM skill WHERE name='Identifying Key Words and Phrases');
