INSERT INTO passage (address, title, text) SELECT '1 Samuel 3:10–14', 'God Speaks to Samuel', 'And the LORD came and stood, calling as at other times, “Samuel! Samuel!” And Samuel said, “Speak, for your servant hears.” Then the LORD said to Samuel, “Behold, I am about to do a thing in Israel at which the two ears of everyone who hears it will tingle. On that day I will fulfill against Eli all that I have spoken concerning his house, from beginning to end. And I declare to him that I am about to punish his house forever, for the iniquity that he knew, because his sons were blaspheming God, and he did not restrain them. Therefore I swear to the house of Eli that the iniquity of Eli’s house shall not be atoned for by sacrifice or offering forever.”' WHERE NOT EXISTS (SELECT 1 FROM passage WHERE address='1 Samuel 3:10–14' AND title='God Speaks to Samuel');
INSERT INTO skill (name, category, learning_stage, subject_id) SELECT 'Identifying Key Words and Phrases', 'VOCABULARY_AND_LANGUAGE', 'FOUNDATIONAL', (SELECT id FROM subject WHERE name='Reading') WHERE NOT EXISTS (SELECT 1 FROM skill WHERE name='Identifying Key Words and Phrases');
