INSERT INTO question (text, question_type, difficulty, metadata, skill_id, passage_id) VALUES ('What phrase does Naaman use after being healed in the Jordan River?', 'MCQ', 'EASY', '{"options":{"A":"Now I know that there is no God in all the earth except in Israel","B":"This water is blessed","C":"I am forever clean","D":"My sickness is gone"},"correctAnswer":"A"}', (SELECT id FROM skill WHERE name='Identifying Key Words and Phrases'), (SELECT id FROM passage WHERE address='2 Kings 5:1–14' AND title='Naaman’s Healing'));
INSERT INTO passage (address, title, text) SELECT 'Daniel 3:16–18', 'Shadrach, Meshach, and Abednego’s Response', 'Shadrach, Meshach, and Abednego answered and said to the king, “O Nebuchadnezzar, we have no need to answer you in this matter. If this be so, our God whom we serve is able to deliver us from the burning fiery furnace, and he will deliver us out of your hand, O king. But if not, be it known to you, O king, that we will not serve your gods or worship the golden image that you have set up.”' WHERE NOT EXISTS (SELECT 1 FROM passage WHERE address='Daniel 3:16–18' AND title='Shadrach, Meshach, and Abednego’s Response');
