INSERT INTO question (text, question_type, difficulty, metadata, skill_id, passage_id) VALUES ('What key phrase shows their unwavering faith in God?', 'MCQ', 'EASY', '{"options":{"A":"We shall call upon His name","B":"We will escape unharmed","C":"Even if He does not","D":"The Lord is mighty"},"correctAnswer":"C"}', (SELECT id FROM skill WHERE name='Identifying Key Words and Phrases'), (SELECT id FROM passage WHERE address='Daniel 3:16–18' AND title='Shadrach, Meshach, and Abednego’s Response'));
INSERT INTO passage (address, title, text) SELECT 'Esther 4:13–16', 'Mordecai’s Message to Esther', 'Then Mordecai told them to reply to Esther, “Do not think to yourself that in the king’s palace you will escape any more than all the other Jews. For if you keep silent at this time, relief and deliverance will rise for the Jews from another place, but you and your father’s house will perish. And who knows whether you have not come to the kingdom for such a time as this?” Then Esther told them to reply to Mordecai, “Go, gather all the Jews to be found in Susa, and hold a fast on my behalf, and do not eat or drink for three days, night or day. I and my young women will also fast as you do. Then I will go to the king, though it is against the law, and if I perish, I perish.”' WHERE NOT EXISTS (SELECT 1 FROM passage WHERE address='Esther 4:13–16' AND title='Mordecai’s Message to Esther');
INSERT INTO skill (name, category, learning_stage, subject_id) SELECT 'Identifying Key Words and Phrases', 'VOCABULARY_AND_LANGUAGE', 'FOUNDATIONAL', (SELECT id FROM subject WHERE name='Reading') WHERE NOT EXISTS (SELECT 1 FROM skill WHERE name='Identifying Key Words and Phrases');
INSERT INTO question (text, question_type, difficulty, metadata, skill_id, passage_id) VALUES ('What phrase suggests that Esther was placed in her position for a divine purpose?', 'MCQ', 'EASY', '{"options":{"A":"A new queen may come","B":"If you remain silent","C":"For such a time as this","D":"God will deliver you"},"correctAnswer":"C"}', (SELECT id FROM skill WHERE name='Identifying Key Words and Phrases'), (SELECT id FROM passage WHERE address='Esther 4:13–16' AND title='Mordecai’s Message to Esther'));
INSERT INTO passage (address, title, text) SELECT 'Matthew 4:18–22', 'Jesus Calls the First Disciples', 'While walking by the Sea of Galilee, he saw two brothers, Simon (who is called Peter) and Andrew his brother, casting a net into the sea, for they were fishermen. And he said to them, “Follow me, and I will make you fishers of men.” Immediately they left their nets and followed him. And going on from there he saw two other brothers, James the son of Zebedee and John his brother, in the boat with Zebedee their father, mending their nets, and he called them. Immediately they left the boat and their father and followed him.' WHERE NOT EXISTS (SELECT 1 FROM passage WHERE address='Matthew 4:18–22' AND title='Jesus Calls the First Disciples');
INSERT INTO skill (name, category, learning_stage, subject_id) SELECT 'Identifying Key Words and Phrases', 'VOCABULARY_AND_LANGUAGE', 'FOUNDATIONAL', (SELECT id FROM subject WHERE name='Reading') WHERE NOT EXISTS (SELECT 1 FROM skill WHERE name='Identifying Key Words and Phrases');
INSERT INTO question (text, question_type, difficulty, metadata, skill_id, passage_id) VALUES ('What phrase did Jesus use to call His disciples?', 'MCQ', 'EASY', '{"options":{"A":"Walk with me","B":"Join my mission","C":"Leave everything behind","D":"Come, follow me"},"correctAnswer":"D"}', (SELECT id FROM skill WHERE name='Identifying Key Words and Phrases'), (SELECT id FROM passage WHERE address='Matthew 4:18–22' AND title='Jesus Calls the First Disciples'));
INSERT INTO passage (address, title, text) SELECT 'Luke 23:32–43', 'Jesus and the Thief on the Cross', 'Two others, who were criminals, were led away to be put to death with him. And when they came to the place that is called The Skull, there they crucified him, and the criminals, one on his right and one on his left. And Jesus said, “Father, forgive them, for they know not what they do.” And they cast lots to divide his garments. And the people stood by, watching, but the rulers scoffed at him, saying, “He saved others; let him save himself, if he is the Christ of God, his Chosen One!” The soldiers also mocked him, coming up and offering him sour wine and saying, “If you are the King of the Jews, save yourself!” There was also an inscription over him, “This is the King of the Jews.” One of the criminals who were hanged railed at him, saying, “Are you not the Christ? Save yourself and us!” But the other rebuked him, saying, “Do you not fear God, since you are under the same sentence of condemnation? And we indeed justly, for we are receiving the due reward of our deeds; but this man has done nothing wrong.” And he said, “Jesus, remember me when you come into your kingdom.” And he said to him, “Truly, I say to you, today you will be with me in paradise.”' WHERE NOT EXISTS (SELECT 1 FROM passage WHERE address='Luke 23:32–43' AND title='Jesus and the Thief on the Cross');
INSERT INTO skill (name, category, learning_stage, subject_id) SELECT 'Identifying Key Words and Phrases', 'VOCABULARY_AND_LANGUAGE', 'FOUNDATIONAL', (SELECT id FROM subject WHERE name='Reading') WHERE NOT EXISTS (SELECT 1 FROM skill WHERE name='Identifying Key Words and Phrases');
