INSERT INTO passage (address, title, text) SELECT 'Romans 8:38–39', 'Paul on God’s Love', 'For I am sure that neither death nor life, nor angels nor rulers, nor things present nor things to come, nor powers, nor height nor depth, nor anything else in all creation, will be able to separate us from the love of God in Christ Jesus our Lord.' WHERE NOT EXISTS (SELECT 1 FROM passage WHERE address='Romans 8:38–39' AND title='Paul on God’s Love');
