INSERT INTO passage (address, title, text) SELECT 'Matthew 7:24–27', 'The Wise and Foolish Builders', '“Everyone then who hears these words of mine and does them will be like a wise man who built his house on the rock. And the rain fell, and the floods came, and the winds blew and beat on that house, but it did not fall, because it had been founded on the rock. And everyone who hears these words of mine and does not do them will be like a foolish man who built his house on the sand. And the rain fell, and the floods came, and the winds blew and beat against that house, and it fell, and great was the fall of it.”' WHERE NOT EXISTS (SELECT 1 FROM passage WHERE address='Matthew 7:24–27' AND title='The Wise and Foolish Builders');
