INSERT INTO passage (address, title, text) SELECT '2 Samuel 7:12–16', 'God’s Covenant with David', 'When your days are fulfilled and you lie down with your fathers, I will raise up your offspring after you, who shall come from your body, and I will establish his kingdom. He shall build a house for my name, and I will establish the throne of his kingdom forever. I will be to him a father, and he shall be to me a son. When he commits iniquity, I will discipline him with the rod of men, with the stripes of the sons of men, but my steadfast love will not depart from him, as I took it from Saul, whom I put away from before you. And your house and your kingdom shall be made sure forever before me. Your throne shall be established forever.’”' WHERE NOT EXISTS (SELECT 1 FROM passage WHERE address='2 Samuel 7:12–16' AND title='God’s Covenant with David');
INSERT INTO skill (name, category, learning_stage, subject_id) SELECT 'Connecting to Other Texts', 'SYNTHESIS_AND_APPLICATION', 'MASTERY', (SELECT id FROM subject WHERE name='Reading') WHERE NOT EXISTS (SELECT 1 FROM skill WHERE name='Connecting to Other Texts');
