INSERT INTO passage (address, title, text) SELECT 'Zechariah 9:9', 'The Triumphal Entry', 'Rejoice greatly, O daughter of Zion! Shout aloud, O daughter of Jerusalem! Behold, your king is coming to you; righteous and having salvation is he, humble and mounted on a donkey, on a colt, the foal of a donkey.' WHERE NOT EXISTS (SELECT 1 FROM passage WHERE address='Zechariah 9:9' AND title='The Triumphal Entry');
