INSERT INTO question (text, question_type, difficulty, metadata, skill_id, passage_id) VALUES ('How does Zechariah 9:9’s prophecy about a king riding on a donkey relate to Jesus?', 'MCQ', 'EASY', '{"options":{"A":"It refers to Solomon’s coronation.","B":"It predicts the exile to Babylon.","C":"Jesus fulfilled this when He entered Jerusalem on a donkey.","D":"It was fulfilled by King David."},"correctAnswer":"C"}', (SELECT id FROM skill WHERE name='Identifying Prophetic Fulfillment'), (SELECT id FROM passage WHERE address='Zechariah 9:9' AND title='The Triumphal Entry'));
INSERT INTO passage (address, title, text) SELECT 'Zechariah 11:12–13', 'The Betrayal for Thirty Pieces of Silver', 'Then I said to them, “If it seems good to you, give me my wages; but if not, keep them.” And they weighed out as my wages thirty pieces of silver. Then the LORD said to me, “Throw it to the potter”—the lordly price at which I was priced by them. So I took the thirty pieces of silver and threw them into the house of the LORD, to the potter.' WHERE NOT EXISTS (SELECT 1 FROM passage WHERE address='Zechariah 11:12–13' AND title='The Betrayal for Thirty Pieces of Silver');
INSERT INTO skill (name, category, learning_stage, subject_id) SELECT 'Identifying Prophetic Fulfillment', 'SYNTHESIS_AND_APPLICATION', 'MASTERY', (SELECT id FROM subject WHERE name='Reading') WHERE NOT EXISTS (SELECT 1 FROM skill WHERE name='Identifying Prophetic Fulfillment');
INSERT INTO question (text, question_type, difficulty, metadata, skill_id, passage_id) VALUES ('Where is Zechariah 11:12–13 fulfilled in the New Testament?', 'MCQ', 'EASY', '{"options":{"A":"In Matthew 27:3–10, when Judas betrays Jesus for thirty pieces of silver.","B":"In Acts 9:1–9, when Saul is converted.","C":"In Luke 22:19, during the Last Supper.","D":"In John 20:21, after Jesus’ resurrection."},"correctAnswer":"A"}', (SELECT id FROM skill WHERE name='Identifying Prophetic Fulfillment'), (SELECT id FROM passage WHERE address='Zechariah 11:12–13' AND title='The Betrayal for Thirty Pieces of Silver'));
INSERT INTO passage (address, title, text) SELECT 'Psalm 22:1–18', 'The Crucifixion Foretold', 'To the choirmaster: according to The Doe of the Dawn. A Psalm of David. My God, my God, why have you forsaken me? Why are you so far from saving me, from the words of my groaning? O my God, I cry by day, but you do not answer, and by night, but I find no rest. Yet you are holy, enthroned on the praises of Israel. In you our fathers trusted; they trusted, and you delivered them. To you they cried and were rescued; in you they trusted and were not put to shame. But I am a worm and not a man, scorned by mankind and despised by the people. All who see me mock me; they make mouths at me; they wag their heads; “He trusts in the LORD; let him deliver him; let him rescue him, for he delights in him!” Yet you are he who took me from the womb; you made me trust you at my mother’s breasts. On you was I cast from my birth, and from my mother’s womb you have been my God. Be not far from me, for trouble is near, and there is none to help. Many bulls encompass me; strong bulls of Bashan surround me; they open wide their mouths at me, like a ravening and roaring lion. I am poured out like water, and all my bones are out of joint; my heart is like wax; it is melted within my breast; my strength is dried up like a potsherd, and my tongue sticks to my jaws; you lay me in the dust of death. For dogs encompass me; a company of evildoers encircles me; they have pierced my hands and feet— I can count all my bones— they stare and gloat over me; they divide my garments among them, and for my clothing they cast lots.' WHERE NOT EXISTS (SELECT 1 FROM passage WHERE address='Psalm 22:1–18' AND title='The Crucifixion Foretold');
INSERT INTO skill (name, category, learning_stage, subject_id) SELECT 'Identifying Prophetic Fulfillment', 'SYNTHESIS_AND_APPLICATION', 'MASTERY', (SELECT id FROM subject WHERE name='Reading') WHERE NOT EXISTS (SELECT 1 FROM skill WHERE name='Identifying Prophetic Fulfillment');
INSERT INTO question (text, question_type, difficulty, metadata, skill_id, passage_id) VALUES ('How does Psalm 22 describe Jesus’ crucifixion?', 'MCQ', 'EASY', '{"options":{"A":"It details His suffering, mocking, and pierced hands and feet.","B":"It describes Solomon’s temple.","C":"It predicts David’s battles.","D":"It speaks of Israel’s exile."},"correctAnswer":"A"}', (SELECT id FROM skill WHERE name='Identifying Prophetic Fulfillment'), (SELECT id FROM passage WHERE address='Psalm 22:1–18' AND title='The Crucifixion Foretold'));
INSERT INTO passage (address, title, text) SELECT 'Daniel 7:13–14', 'The Son of Man Prophecy', '“I saw in the night visions, and behold, with the clouds of heaven there came one like a son of man, and he came to the Ancient of Days and was presented before him. And to him was given dominion and glory and a kingdom, that all peoples, nations, and languages should serve him; his dominion is an everlasting dominion, which shall not pass away, and his kingdom one that shall not be destroyed.' WHERE NOT EXISTS (SELECT 1 FROM passage WHERE address='Daniel 7:13–14' AND title='The Son of Man Prophecy');
