INSERT INTO question (text, question_type, difficulty, metadata, skill_id, passage_id) VALUES ('How does Jesus fulfill the Son of Man prophecy in Daniel 7:13–14?', 'MCQ', 'EASY', '{"options":{"A":"It predicts John the Baptist’s ministry.","B":"Jesus identifies Himself as the Son of Man with eternal dominion.","C":"It refers to King Cyrus restoring Israel.","D":"It is about the rebuilding of the temple."},"correctAnswer":"B"}', (SELECT id FROM skill WHERE name='Identifying Prophetic Fulfillment'), (SELECT id FROM passage WHERE address='Daniel 7:13–14' AND title='The Son of Man Prophecy'));
