INSERT INTO question (text, question_type, difficulty, metadata, skill_id, passage_id) VALUES ('How does Philippians 4:6–7 encourage us in anxiety?', 'MCQ', 'EASY', '{"options":{"A":"Stress is unavoidable and necessary.","B":"Prayer brings peace that surpasses understanding.","C":"Only leaders should pray about worries.","D":"Anxiety means we lack faith."},"correctAnswer":"B"}', (SELECT id FROM skill WHERE name='Drawing Personal Connections'), (SELECT id FROM passage WHERE address='Philippians 4:6–7' AND title='Do Not Be Anxious'));
INSERT INTO passage (address, title, text) SELECT 'Hebrews 12:1–3', 'Run the Race with Perseverance', 'Therefore, since we are surrounded by so great a cloud of witnesses, let us also lay aside every weight, and sin which clings so closely, and let us run with endurance the race that is set before us, looking to Jesus, the founder and perfecter of our faith, who for the joy that was set before him endured the cross, despising the shame, and is seated at the right hand of the throne of God. Consider him who endured from sinners such hostility against himself, so that you may not grow weary or fainthearted.' WHERE NOT EXISTS (SELECT 1 FROM passage WHERE address='Hebrews 12:1–3' AND title='Run the Race with Perseverance');
INSERT INTO skill (name, category, learning_stage, subject_id) SELECT 'Drawing Personal Connections', 'SYNTHESIS_AND_APPLICATION', 'MASTERY', (SELECT id FROM subject WHERE name='Reading') WHERE NOT EXISTS (SELECT 1 FROM skill WHERE name='Drawing Personal Connections');
INSERT INTO question (text, question_type, difficulty, metadata, skill_id, passage_id) VALUES ('How does the race metaphor apply to faith?', 'MCQ', 'EASY', '{"options":{"A":"Faith requires endurance and perseverance.","B":"Only pastors need endurance.","C":"Winning is the only important part.","D":"Faith is easy and effortless."},"correctAnswer":"A"}', (SELECT id FROM skill WHERE name='Drawing Personal Connections'), (SELECT id FROM passage WHERE address='Hebrews 12:1–3' AND title='Run the Race with Perseverance'));
INSERT INTO passage (address, title, text) SELECT 'Genesis 22:1–19', 'Abraham’s Test and Jesus’ Sacrifice', 'After these things God tested Abraham and said to him, “Abraham!” And he said, “Here I am.” He said, “Take your son, your only son Isaac, whom you love, and go to the land of Moriah, and offer him there as a burnt offering on one of the mountains of which I shall tell you.” So Abraham rose early in the morning, saddled his donkey, and took two of his young men with him, and his son Isaac. And he cut the wood for the burnt offering and arose and went to the place of which God had told him. On the third day Abraham lifted up his eyes and saw the place from afar. Then Abraham said to his young men, “Stay here with the donkey; I and the boy will go over there and worship and come again to you.” And Abraham took the wood of the burnt offering and laid it on Isaac his son. And he took in his hand the fire and the knife. So they went both of them together. And Isaac said to his father Abraham, “My father!” And he said, “Here I am, my son.” He said, “Behold, the fire and the wood, but where is the lamb for a burnt offering?” Abraham said, “God will provide for himself the lamb for a burnt offering, my son.” So they went both of them together. When they came to the place of which God had told him, Abraham built the altar there and laid the wood in order and bound Isaac his son and laid him on the altar, on top of the wood. Then Abraham reached out his hand and took the knife to slaughter his son. But the angel of the LORD called to him from heaven and said, “Abraham, Abraham!” And he said, “Here I am.” He said, “Do not lay your hand on the boy or do anything to him, for now I know that you fear God, seeing you have not withheld your son, your only son, from me.” And Abraham lifted up his eyes and looked, and behold, behind him was a ram, caught in a thicket by his horns. And Abraham went and took the ram and offered it up as a burnt offering instead of his son. So Abraham called the name of that place, “The LORD will provide”; as it is said to this day, “On the mount of the LORD it shall be provided.” And the angel of the LORD called to Abraham a second time from heaven and said, “By myself I have sworn, declares the LORD, because you have done this and have not withheld your son, your only son, I will surely bless you, and I will surely multiply your offspring as the stars of heaven and as the sand that is on the seashore. And your offspring shall possess the gate of his enemies, and in your offspring shall all the nations of the earth be blessed, because you have obeyed my voice.” So Abraham returned to his young men, and they arose and went together to Beersheba. And Abraham lived at Beersheba.' WHERE NOT EXISTS (SELECT 1 FROM passage WHERE address='Genesis 22:1–19' AND title='Abraham’s Test and Jesus’ Sacrifice');
INSERT INTO skill (name, category, learning_stage, subject_id) SELECT 'Recognizing Parallels Between Old and New Testament', 'BIBLICAL_CONNECTIONS', 'MASTERY', (SELECT id FROM subject WHERE name='Reading') WHERE NOT EXISTS (SELECT 1 FROM skill WHERE name='Recognizing Parallels Between Old and New Testament');
INSERT INTO question (text, question_type, difficulty, metadata, skill_id, passage_id) VALUES ('How does Abraham’s offering of Isaac parallel Jesus’ sacrifice?', 'MCQ', 'EASY', '{"options":{"A":"Both sacrifices were commanded by Moses.","B":"Both involve a literal lamb being slain.","C":"Both involve a father offering his son.","D":"Both sacrifices occurred in Jerusalem."},"correctAnswer":"C"}', (SELECT id FROM skill WHERE name='Recognizing Parallels Between Old and New Testament'), (SELECT id FROM passage WHERE address='Genesis 22:1–19' AND title='Abraham’s Test and Jesus’ Sacrifice'));
INSERT INTO passage (address, title, text) SELECT 'Exodus 12:1–30', 'Passover and Jesus as the Lamb', 'The LORD said to Moses and Aaron in the land of Egypt, “This month shall be for you the beginning of months. It shall be the first month of the year for you. Tell all the congregation of Israel that on the tenth day of this month every man shall take a lamb according to their fathers’ houses, a lamb for a household. And if the household is too small for a lamb, then he and his nearest neighbor shall take according to the number of persons; according to what each can eat you shall make your count for the lamb. Your lamb shall be without blemish, a male a year old. You may take it from the sheep or from the goats, and you shall keep it until the fourteenth day of this month, when the whole assembly of the congregation of Israel shall kill their lambs at twilight. “Then they shall take some of the blood and put it on the two doorposts and the lintel of the houses in which they eat it. They shall eat the flesh that night, roasted on the fire; with unleavened bread and bitter herbs they shall eat it. Do not eat any of it raw or boiled in water, but roasted, its head with its legs and its inner parts. And you shall let none of it remain until the morning; anything that remains until the morning you shall burn. In this manner you shall eat it: with your belt fastened, your sandals on your feet, and your staff in your hand. And you shall eat it in haste. It is the LORD’s Passover. For I will pass through the land of Egypt that night, and I will strike all the firstborn in the land of Egypt, both man and beast; and on all the gods of Egypt I will execute judgments: I am the LORD. The blood shall be a sign for you, on the houses where you are. And when I see the blood, I will pass over you, and no plague will befall you to destroy you, when I strike the land of Egypt. “This day shall be for you a memorial day, and you shall keep it as a feast to the LORD; throughout your generations, as a statute forever, you shall keep it as a feast. Seven days you shall eat unleavened bread. On the first day you shall remove leaven out of your houses, for if anyone eats what is leavened, from the first day until the seventh day, that person shall be cut off from Israel. On the first day you shall hold a holy assembly, and on the seventh day a holy assembly. No work shall be done on those days. But what everyone needs to eat, that alone may be prepared by you. And you shall observe the Feast of Unleavened Bread, for on this very day I brought your hosts out of the land of Egypt. Therefore you shall observe this day, throughout your generations, as a statute forever. In the first month, from the fourteenth day of the month at evening, you shall eat unleavened bread until the twenty-first day of the month at evening. For seven days no leaven is to be found in your houses. If anyone eats what is leavened, that person will be cut off from the congregation of Israel, whether he is a sojourner or a native of the land. You shall eat nothing leavened; in all your dwelling places you shall eat unleavened bread.” Then Moses called all the elders of Israel and said to them, “Go and select lambs for yourselves according to your clans, and kill the Passover lamb. Take a bunch of hyssop and dip it in the blood that is in the basin, and touch the lintel and the two doorposts with the blood that is in the basin. None of you shall go out of the door of his house until the morning. For the LORD will pass through to strike the Egyptians, and when he sees the blood on the lintel and on the two doorposts, the LORD will pass over the door and will not allow the destroyer to enter your houses to strike you. You shall observe this rite as a statute for you and for your sons forever. And when you come to the land that the LORD will give you, as he has promised, you shall keep this service. And when your children say to you, ‘What do you mean by this service?’ you shall say, ‘It is the sacrifice of the LORD’s Passover, for he passed over the houses of the people of Israel in Egypt, when he struck the Egyptians but spared our houses.’” And the people bowed their heads and worshiped. Then the people of Israel went and did so; as the LORD had commanded Moses and Aaron, so they did. At midnight the LORD struck down all the firstborn in the land of Egypt, from the firstborn of Pharaoh who sat on his throne to the firstborn of the captive who was in the dungeon, and all the firstborn of the livestock. And Pharaoh rose up in the night, he and all his servants and all the Egyptians. And there was a great cry in Egypt, for there was not a house where someone was not dead.' WHERE NOT EXISTS (SELECT 1 FROM passage WHERE address='Exodus 12:1–30' AND title='Passover and Jesus as the Lamb');
INSERT INTO skill (name, category, learning_stage, subject_id) SELECT 'Recognizing Parallels Between Old and New Testament', 'BIBLICAL_CONNECTIONS', 'MASTERY', (SELECT id FROM subject WHERE name='Reading') WHERE NOT EXISTS (SELECT 1 FROM skill WHERE name='Recognizing Parallels Between Old and New Testament');
INSERT INTO question (text, question_type, difficulty, metadata, skill_id, passage_id) VALUES ('How does the Passover lamb foreshadow Jesus?', 'MCQ', 'EASY', '{"options":{"A":"Jesus is called the ''Lamb of God'' who takes away sin.","B":"Jesus instituted Passover as a Christian practice.","C":"Jesus was born during Passover.","D":"Jesus performed the first Passover."},"correctAnswer":"A"}', (SELECT id FROM skill WHERE name='Recognizing Parallels Between Old and New Testament'), (SELECT id FROM passage WHERE address='Exodus 12:1–30' AND title='Passover and Jesus as the Lamb'));
INSERT INTO passage (address, title, text) SELECT 'Numbers 21:4–9', 'The Bronze Serpent and Jesus', 'From Mount Hor they set out by the way to the Red Sea, to go around the land of Edom. And the people became impatient on the way. And the people spoke against God and against Moses, “Why have you brought us up out of Egypt to die in the wilderness? For there is no food and no water, and we loathe this worthless food.” Then the LORD sent fiery serpents among the people, and they bit the people, so that many people of Israel died. And the people came to Moses and said, “We have sinned, for we have spoken against the LORD and against you. Pray to the LORD, that he take away the serpents from us.” So Moses prayed for the people. And the LORD said to Moses, “Make a fiery serpent and set it on a pole, and everyone who is bitten, when he sees it, shall live.” So Moses made a bronze serpent and set it on a pole. And if a serpent bit anyone, he would look at the bronze serpent and live.' WHERE NOT EXISTS (SELECT 1 FROM passage WHERE address='Numbers 21:4–9' AND title='The Bronze Serpent and Jesus');
INSERT INTO skill (name, category, learning_stage, subject_id) SELECT 'Recognizing Parallels Between Old and New Testament', 'BIBLICAL_CONNECTIONS', 'MASTERY', (SELECT id FROM subject WHERE name='Reading') WHERE NOT EXISTS (SELECT 1 FROM skill WHERE name='Recognizing Parallels Between Old and New Testament');
INSERT INTO question (text, question_type, difficulty, metadata, skill_id, passage_id) VALUES ('What parallel does Jesus make between the bronze serpent and Himself?', 'MCQ', 'EASY', '{"options":{"A":"He says in John 3:14 that He will be ''lifted up'' like the serpent.","B":"He compares the serpent to John the Baptist.","C":"He says the serpent healed people permanently.","D":"He says the serpent represents Satan."},"correctAnswer":"A"}', (SELECT id FROM skill WHERE name='Recognizing Parallels Between Old and New Testament'), (SELECT id FROM passage WHERE address='Numbers 21:4–9' AND title='The Bronze Serpent and Jesus'));
INSERT INTO passage (address, title, text) SELECT 'Deuteronomy 18:15–19', 'Moses and Jesus as Prophets', '“The LORD your God will raise up for you a prophet like me from among you, from your brothers—it is to him you shall listen—just as you desired of the LORD your God at Horeb on the day of the assembly, when you said, ‘Let me not hear again the voice of the LORD my God or see this great fire any more, lest I die.’ And the LORD said to me, ‘They are right in what they have spoken. I will raise up for them a prophet like you from among their brothers. And I will put my words in his mouth, and he shall speak to them all that I command him. And whoever will not listen to my words that he shall speak in my name, I myself will require it of him.' WHERE NOT EXISTS (SELECT 1 FROM passage WHERE address='Deuteronomy 18:15–19' AND title='Moses and Jesus as Prophets');
INSERT INTO skill (name, category, learning_stage, subject_id) SELECT 'Recognizing Parallels Between Old and New Testament', 'BIBLICAL_CONNECTIONS', 'MASTERY', (SELECT id FROM subject WHERE name='Reading') WHERE NOT EXISTS (SELECT 1 FROM skill WHERE name='Recognizing Parallels Between Old and New Testament');
INSERT INTO question (text, question_type, difficulty, metadata, skill_id, passage_id) VALUES ('How is Jesus similar to Moses according to Deuteronomy 18?', 'MCQ', 'EASY', '{"options":{"A":"Jesus led Israel out of Egypt.","B":"Jesus and Moses both established synagogues.","C":"Jesus wrote the Ten Commandments.","D":"Jesus is the prophet like Moses, as mentioned in Acts 3:22."},"correctAnswer":"D"}', (SELECT id FROM skill WHERE name='Recognizing Parallels Between Old and New Testament'), (SELECT id FROM passage WHERE address='Deuteronomy 18:15–19' AND title='Moses and Jesus as Prophets'));
INSERT INTO passage (address, title, text) SELECT '1 Kings 17:8–16', 'Elijah and Jesus'' Miraculous Provision', 'Then the word of the LORD came to him, “Arise, go to Zarephath, which belongs to Sidon, and dwell there. Behold, I have commanded a widow there to feed you.” So he arose and went to Zarephath. And when he came to the gate of the city, behold, a widow was there gathering sticks. And he called to her and said, “Bring me a little water in a vessel, that I may drink.” And as she was going to bring it, he called to her and said, “Bring me a morsel of bread in your hand.” And she said, “As the LORD your God lives, I have nothing baked, only a handful of flour in a jar and a little oil in a jug. And now I am gathering a couple of sticks that I may go in and prepare it for myself and my son, that we may eat it and die.” And Elijah said to her, “Do not fear; go and do as you have said. But first make me a little cake of it and bring it to me, and afterward make something for yourself and your son. For thus says the LORD, the God of Israel, ‘The jar of flour shall not be spent, and the jug of oil shall not be empty, until the day that the LORD sends rain upon the earth.’” And she went and did as Elijah said. And she and he and her household ate for many days. The jar of flour was not spent, neither did the jug of oil become empty, according to the word of the LORD that he spoke by Elijah.' WHERE NOT EXISTS (SELECT 1 FROM passage WHERE address='1 Kings 17:8–16' AND title='Elijah and Jesus'' Miraculous Provision');
INSERT INTO skill (name, category, learning_stage, subject_id) SELECT 'Recognizing Parallels Between Old and New Testament', 'BIBLICAL_CONNECTIONS', 'MASTERY', (SELECT id FROM subject WHERE name='Reading') WHERE NOT EXISTS (SELECT 1 FROM skill WHERE name='Recognizing Parallels Between Old and New Testament');
INSERT INTO question (text, question_type, difficulty, metadata, skill_id, passage_id) VALUES ('How is Elijah’s provision for the widow similar to Jesus'' miracles?', 'MCQ', 'EASY', '{"options":{"A":"Both raised a widow’s son from the dead.","B":"Both performed miracles in Bethlehem.","C":"Both fasted for forty days.","D":"Both provided food miraculously to those in need."},"correctAnswer":"D"}', (SELECT id FROM skill WHERE name='Recognizing Parallels Between Old and New Testament'), (SELECT id FROM passage WHERE address='1 Kings 17:8–16' AND title='Elijah and Jesus'' Miraculous Provision'));
